mapper.select:
      select rnum, boardno, btitle, bcontent, userid, bdate, bcount, originno, grouplayer, status
      from(
         select rownum as rnum, boardno, btitle, bcontent, userid, bdate, bcount, originno, grouplayer, status
            from (
               select boardno, btitle, bcontent, userid, bdate, bcount, originno, grouplayer, status
               from communityqnas



					where btitle like '%' || #{keyword} || '%' or bcontent like '%' || #{keyword} || '%'


					where userid like '%' || #{keyword} || '%'

               order by originno desc, boardno asc
            )
         where rownum <=#{endRowNo}
      )
      where rnum >= #{startRowNo}

select boardno, btitle, bcontent, userid, bdate, bcount, originno, grouplayer, status
from communityqnas
where boardno=#{boardno}

select rnum, boardno, btitle, bcontent, userid, bdate, bcount, originno, grouplayer, status
from(
   select rownum as rnum, boardno, btitle, bcontent, userid, bdate, bcount, originno, grouplayer, status
      from (
         select boardno, btitle, bcontent, userid, bdate, bcount, originno, grouplayer, status
         from communityqnas
          where userid=#{userid}
         order by boardno desc
      )

   where rownum <=#{endRowNo}
)
where rnum >= #{startRowNo}

mapper.update:
update communityqnas set
   btitle=#{btitle},
   bcontent=#{bcontent}
   where boardno=#{boardno}

mapper.select:
select count(*) 
from communityqnas
where userid=#{userid}
